package assignment_one;

public class If_Else_Assignment3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int marks1=47;
		if(marks1 >=90 & marks1 <=100)
			System.out.println("Grade A:"+marks1);
		else if(marks1 >=75 & marks1<=90)
			System.out.println("Grade B:"+marks1);
		else if(marks1 >=50 & marks1 <=74)
			System.out.println("Grade C:"+marks1);
		else
			System.out.println("Grade Fail:"+marks1);
				

	}

}
