package assignment;

public class data_type_assignment_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte value=10;
		System.out.println("Byte value:" +value);
		
		short num=100;
		System.out.println("Short value:" +num);
		
		int num1 =5000;
		System.out.println("Int Value:" +num1);
		
		long value1=90000000000L;
		System.out.println("Long Value:"+value1);
		
		float value3=168.02f;
		System.out.println("Flaot value:" +value3);
		
		double data=99.99;
		System.out.println("Double Value:" +data);
		
		char letter='A';
		System.out.println("Char Value:" +letter);
		
		boolean logic=true;
		System.out.println("Boolean value:" +logic);
		

	}

}
