package assignment_one;

public class loop_assignment_6_factorial_number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int Number=6;
		int i;
		long Fctl=1;
		
		
		for(i=1;i<=Number;i++)
			Fctl=Fctl *i;
		{
			System.out.println("Factorial of given number:"+Fctl);		
			}
		
		

	}

}
