package assignment_one;

public class loop_assignment_while_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int count=0; int num=56879; 
		while(num !=0)
	
		{
			num /=10;
			++count;
		}
		
		System.out.println("Number of digits:"+count);
			
		
		
		

	}

}
